import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
:root {
  --background: #f0f2f5;
  --green: #33CC95;
  --red: #E52E4D;
  --blue: #5429CC;

  --blue-light: #6933FF;

  --text-title: #363F5F;
  --text-body: #969CB3;

  --background: #F0F2F5;
  --shape: #FFFFFF;
                      }


`;
